DROP TABLE IF EXISTS `onethink_case`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `onethink_case` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `case_number` char(20) DEFAULT NULL COMMENT '案件编号',
  `area_code` int(10) DEFAULT NULL COMMENT '区编码',
  `street_code` int(10) DEFAULT NULL COMMENT '街/镇编号',
  `community_code` int(10) DEFAULT NULL COMMENT '村/社区编号',
  `name` varchar(15) DEFAULT NULL COMMENT '姓名',
  `sex` tinyint(1) DEFAULT NULL COMMENT '性别(1:女 2:男 )',
  `nation` varchar(50) DEFAULT NULL COMMENT '所属民族',
  `birthday` varchar(15) DEFAULT NULL COMMENT '出生年月，格式(2005-12) 处理时使用分割函数分别获得年和月',
  `photo` text COMMENT '照片',
  `household_pro_code` int(10) DEFAULT NULL COMMENT '户籍所在地：省份编码',
  `household_city_code` int(10) DEFAULT NULL COMMENT '户籍所在地：市编码',
  `household_area_code` int(10) DEFAULT NULL COMMENT '户籍所在地：区/县编码',
  `home_pro_code` int(10) DEFAULT NULL COMMENT '家庭住址：省份编码',
  `home_city_code` int(10) DEFAULT NULL COMMENT '家庭住址：市编码',
  `home_area_code` int(10) DEFAULT NULL COMMENT '家庭住址：详细地址',
  `home_address` varchar(255) NOT NULL COMMENT '家庭住址：',
  `health` text COMMENT '健康状况',
  `character` text COMMENT '性格状况',
  `admission_status` text COMMENT '入学状况',
  `entrance` char(20) DEFAULT NULL COMMENT '学校',
  `family_jianhuren` text COMMENT '是否是监护人',
  `family_members` text COMMENT '家庭成员(使用序列化数组保存)',
  `family_structure` tinyint(1) DEFAULT NULL COMMENT '家庭结构',
  `guardianship` tinyint(1) DEFAULT NULL COMMENT '监护状况',
  `life_status` tinyint(1) DEFAULT NULL COMMENT '生活情境',
  `enjoy_relief_type` text COMMENT '享受救助类型',
  `housing_type` tinyint(1) DEFAULT NULL COMMENT '住房类型',
  `inner_predicament` text COMMENT '内心困境',
  `Heart_other` char(50) DEFAULT NULL,
  `growth_dilemma` text NOT NULL COMMENT '成长困境及风险等级',
  `growth_dilemmas` text COMMENT '初审风险评估',
  `main_dilemma` text COMMENT '主要困境描述',
  `fill_in_person` varchar(15) DEFAULT NULL COMMENT '填表人',
  `fill_in_time` varchar(15) DEFAULT NULL COMMENT '填表日期',
  `trial_status` tinyint(1) NOT NULL COMMENT '初审状态',
  `trial_time` datetime DEFAULT NULL COMMENT '初审日期',
  `last_instance_status` tinyint(1) NOT NULL COMMENT '终审状态',
  `trial_person` int(10) DEFAULT NULL COMMENT '初审人',
  `last_instance_time` datetime DEFAULT NULL COMMENT '审批时间',
  `last_instance_person` int(10) DEFAULT NULL COMMENT '终审人',
  `trial_suggestion` varchar(255) DEFAULT NULL COMMENT '初审意见',
  `last_instance_instance` varchar(255) NOT NULL COMMENT '终审意见',
  `dispatch_instance` varchar(255) NOT NULL COMMENT '调度意见',
  `dispatch_time` datetime DEFAULT NULL COMMENT '调度时间',
  `dispatch_person` varchar(255) DEFAULT NULL COMMENT '调度人',
  `checkbox_status` text COMMENT '调度选项',
  `police_station` tinyint(1) DEFAULT NULL COMMENT '报警委任部门',
  `turn_professional` tinyint(1) DEFAULT NULL COMMENT '转专业机构',
  `turn_related` tinyint(1) DEFAULT NULL COMMENT '转相关部门',
  `special_community` tinyint(1) DEFAULT NULL COMMENT '专社区服务',
  `transfer_assistance` tinyint(1) DEFAULT NULL COMMENT '专中心救助',
  `management_status` tinyint(1) NOT NULL COMMENT '处置状况',
  `management_record` text COMMENT '处置记录',
  `dilemma_review` varchar(100) DEFAULT NULL COMMENT '困境回顾',
  `help_situation` varchar(100) DEFAULT NULL COMMENT '提供帮扶情况',
  `professional_Reflect` varchar(100) DEFAULT NULL COMMENT '专业反思',
  `recommendations` varchar(100) DEFAULT NULL COMMENT '目标评估及建议',
  `finish_time` datetime DEFAULT NULL COMMENT '结案时间',
  `finish_suggestion` varchar(255) NOT NULL COMMENT '结案意见',
  `deal_with_time` datetime DEFAULT NULL COMMENT '处置时间',
  `visit_status` tinyint(1) DEFAULT NULL COMMENT '回访方式',
  `visit_form` tinyint(1) DEFAULT NULL COMMENT '回访形式选择',
  `visit_way` text COMMENT '回访形式',
  `visit_suggestion` varchar(255) NOT NULL COMMENT '回访记录',
  `visit_time` datetime DEFAULT NULL COMMENT '回访时间',
  `case_status` char(10) NOT NULL COMMENT '案件状态',
  UNIQUE KEY `id` (`id`),
  UNIQUE KEY `case_number` (`case_number`)
) ENGINE=InnoDB AUTO_INCREMENT=446 DEFAULT CHARSET=utf8 COMMENT='未成年保护案件,当事人信息表';
